INSERT INTO `bot` (`id`, `creator_id`, `bot_name`, `description`, `code`, `create_time`, `update_time`) VALUES (9, 4, '知道避障的Bot', '我现在更强了', 'package com.anglyao.botRunningSystem.utils;\n\nimport java.util.ArrayList;\nimport java.util.List;\n\npublic class Bot implements com.anglyao.botRunningSystem.utils.BotInterface {\n    public static class Cell {\n        public int x, y;\n        public Cell(int x, int y) {\n            this.x = x;\n            this.y = y;\n        }\n    }\n    private boolean check_tail_increasing(int step) {  // 检验当前回合，蛇的长度是否增加\n        if (step <= 10) return true;\n        return step % 3 == 1;\n    }\n\n    public List<Cell> getCells(int sx, int sy, String steps) {\n        steps = steps.substring(1, steps.length() - 1);\n        List<Cell> res = new ArrayList<>();\n\n        int[] dx = {-1, 0, 1, 0}, dy = {0, 1, 0, -1};\n        int x = sx, y = sy;\n        int step = 0;\n        res.add(new Cell(x, y));\n        for (int i = 0; i < steps.length(); i ++) {\n            int d = steps.charAt(i) - \'0\';\n            x += dx[d];\n            y += dy[d];\n            res.add(new Cell(x, y));\n            if (!check_tail_increasing( ++ step)) {\n                res.remove(0);\n            }\n        }\n        return res;\n    }\n    @Override\n    public Integer nextMove(String input) {\n        String[] strs = input.split(\"#\");\n        int[][] g = new int[13][14];\n        for(int i = 0, k = 0; i < 13; i ++) {\n            for(int j = 0; j < 14; j ++, k ++) {\n                if (strs[0].charAt(k) == \'1\') {\n                    g[i][j] = 1;\n                }\n            }\n        }\n        int aSx = Integer.parseInt(strs[1]), aSy = Integer.parseInt(strs[2]);\n        List<Cell> cellsA = getCells(aSx, aSy, strs[3]);\n        int bSx = Integer.parseInt(strs[4]), bSy = Integer.parseInt(strs[5]);\n        List<Cell> cellsB = getCells(bSx, bSy, strs[6]);\n        // 把蛇身体存在的位置标记为不能走\n        for(Cell cell: cellsA) {\n            g[cell.x][cell.y] = 1;\n        }\n        for(Cell cell: cellsB) {\n            g[cell.x][cell.y] = 1;\n        }\n        int[] dx = {-1, 0, 1, 0}, dy = {0, 1, 0, -1};\n        // 找一个能走的方向\n        for(int i = 0; i < 4; i ++) {\n            int x = cellsA.get(cellsA.size() - 1).x + dx[i];\n            int y = cellsA.get(cellsA.size() - 1).y + dy[i];\n            if (x >= 0 && x < 13 && y >= 0 && y < 14 && g[x][y] == 0) {\n                return i;\n            }\n        }\n        return 0;\n    }\n}\n', '2026-02-24 22:03:15', '2026-02-24 22:11:20');
